
import logging
from collections import Counter

logging.basicConfig(filename = 'MakeAnagrams_log.txt', 
                    level = logging.DEBUG, 
                    format = '%(asctime)s-%(levelname)s-%(message)s')

#Find the number needed to make anagrams from two input strings
def no_needed(a, b):
    a_map = create_char_map(a)
    b_map = create_char_map(b)
    char_set = []
    char_set = set(a_map.keys()).union(set(b_map.keys()))
    count = 0
    for key in char_set:
        diff = abs(a_map.get(key,0) - b_map.get(key,0))
        count = count + diff
    return count

def create_char_map(char_str):
    char_map = {}
    for char in char_str:
        char_map[char] = char_map.get(char, 0) + 1
    return char_map

#Find the number needed to make anagrams from two input strings using Counters
def no_needed_2(a, b):
    count = 0
    a_ctr = Counter(a)
    b_ctr = Counter(b)
    a_ctr.subtract(b_ctr)
    for key in a_ctr:
        count = count + abs(a_ctr[key])
    return count


